sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
	data.records = [];
	data.userID = gs.getUserID();
	var gr = new GlideRecord('sn_contingent_mgmt_contract_worker');
	gr.addQuery('contingent_request_manager', gs.getUserID());
	gr.addQuery('isrenewalrequested','rejected');
	gr.query();
	data.header = ['Worker Name','Start Date','End Date','Contract Owner','Rejection comments'];

	while (gr.next()) {
		var record = {
			workerName: gr.worker_name.getDisplayValue(),
			startDate: gr.start_date.getDisplayValue(),
			endDate: gr.end_date.getDisplayValue(),
			manager: gr.contract_owner.getDisplayValue(),
			rejectionComments: gr.rejection_comments.getDisplayValue(),
			sys_id: gr.getUniqueValue()
		};
		data.records.push(record);
	}
})();
