sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

    var userSysId = current.worker_name.toString();
    var userGR = new GlideRecord('sys_user');
    if (userGR.get(userSysId)) {
        var referencedSysId = userGR.getUniqueValue();
        gs.info('Sys ID of the referenced user: ' + referencedSysId);

        var userId = userSysId; 
        var roleName = 'sn_contingent_mgmt.contingent_worker'; 
        var userHasRole = new GlideRecord('sys_user_has_role');

        userHasRole.addQuery('user', userId);
        userHasRole.addQuery('role.name', roleName);
        userHasRole.query();

        // Check if any records were found
        if (userHasRole.next()) {
            // User has the specified role
            gs.info('User has the role: ' + roleName);
        } else {
            // User does not have the specified role
			current.setAbortAction(true);
			gs.addErrorMessage("Selected user does not have the worker role!");
            gs.info('User does not have the role: ' + roleName);
        }
    }

})(current, previous);
