sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

    // Add your code here

    gs.info('Hello');
    // Query the "sn_contingent_mgmt_req_worker_map" table
    var workerMapGr = new GlideRecord('sn_contingent_mgmt_req_worker_map');
    workerMapGr.addQuery('contingent_request', current.sys_id);
    workerMapGr.query();

    // Check if any records are found
    if (workerMapGr.next()) {

        // Get the contract_worker reference field from the fetched record
        var contractWorker = workerMapGr.contract_worker;

        // Now you can perform further actions with the contract_worker variable
        // For example, you can use it to update other records or trigger additional workflows

        gs.info('Contract Worker: ' + contractWorker);
		var contractWorkerSysId = contractWorker;
		var contingentRequestSysId = current.sys_id;

        if (contractWorkerSysId && contingentRequestSysId) {

            // Get the "sn_contingent_mgmt_contract_worker" record by sys_id
            var contractWorkerGr = new GlideRecord('sn_contingent_mgmt_contract_worker');
            if (contractWorkerGr.get(contractWorkerSysId)) {

                // Update the fields on the record
                contractWorkerGr.setValue('employment_status', 'Employed');
                contractWorkerGr.setValue('contingent_request', contingentRequestSysId);
				contractWorkerGr.setValue('start_date',current.start_date);
				contractWorkerGr.setValue('end_date',current.end_date);
				contractWorkerGr.setValue('contingent_request_manager',current.contingent_mgr);

                // Save the changes
                contractWorkerGr.update();
                gs.info('Contract Worker record updated successfully.');

            } else {
                gs.error('Contract Worker record not found with sys_id: ' + contractWorkerSysId);
            }

        } else {
            gs.error('Both contractWorkerSysId and contingentRequestSysId are required.');
        }

    } else {
        gs.info('No matching records found in sn_contingent_mgmt_req_worker_map for contingent_request: ' + current.sys_id);
    }
})(current, previous);
