sys_script_client.script:
function onLoad() {
    var collectionID = getParameterValue('sysparm_collectionID');

    // Check if the parameter has a value
    if (collectionID) {
		g_form.setValue('contingent_request', collectionID);
		g_form.setReadOnly('contingent_request', true);
    } else {
        alert('No value got');
    }

}

function getParameterValue(name) {
    var url = top.location.href;
    var value = new URLSearchParams(url).get(name);
    if (value) {
        return value;
    }
    if (!value) {
        var gUrl = new GlideURL();
        gUrl.setFromCurrent();
        value = gUrl.getParam("sysparm_collectionID");
        return value;
    }
}
